mapper.update:
UPDATE assign_submission
SET
    ASSU_SCORE = #{assuScore, jdbcType=NUMERIC}
WHERE
    ASSU_CD = #{assuCd}

mapper.select:
	SELECT COUNT(ASSI_CD)
    FROM ASSIGN_SUBMISSION
    WHERE LEC_CD = #{lecCd} AND ASSI_CD = #{assiCd}

	SELECT assi_nm
    FROM ASSIGNMENT
    WHERE ASSI_CD = #{assiCd}

mapper.update:
UPDATE enrollment e
SET e.gra_assi_score = (
    SELECT SUM(assu_score)
    FROM assign_submission a
    WHERE a.lec_cd = #{lecCd, jdbcType=VARCHAR} AND a.st_cd = #{stCd, jdbcType=VARCHAR}
)
WHERE e.lec_cd = #{lecCd} AND e.st_cd = #{stCd}

mapper.insert:

		SELECT ASSIGN_SUBMISSION_SEQ.NEXTVAL FROM DUAL		

	INSERT INTO assign_submission (
    assu_cd
    , lec_cd
    , assi_cd
    , st_cd
    , atch_file_id
    , subm_date
    , subm_stat
    , assu_score
    , assu_text
	) VALUES (
	 #{assuCd,jdbcType=VARCHAR}
	,#{lecCd,jdbcType=VARCHAR}
	,#{assiCd,jdbcType=VARCHAR}
	,#{stCd,jdbcType=VARCHAR}
	,#{atchFileId,jdbcType=NUMERIC}
	,sysdate
	,#{submStat,jdbcType=VARCHAR}
	,0
	,#{assuText,jdbcType=VARCHAR}
	)

mapper.update:
	UPDATE assign_submission
	SET
      LEC_CD =  #{lecCd,jdbcType=VARCHAR}
    , ASSI_CD =  #{assiCd,jdbcType=VARCHAR}
	, ST_CD =  #{stCd,jdbcType=VARCHAR}
	, ATCH_FILE_ID =  #{atchFileId,jdbcType=NUMERIC}
	, SUBM_DATE = sysdate
	, SUBM_STAT =  #{submStat,jdbcType=VARCHAR}
	, ASSU_SCORE =  #{assuScore,jdbcType=NUMERIC}
	, ASSU_TEXT =  #{assuText,jdbcType=VARCHAR}
	WHERE
    assu_cd = #{assuCd}
