mapper.update:
UPDATE INTERNSHIP SET 
     ATCH_FILE_ID = #{atchFileId,jdbcType=NUMERIC}
    , INTE_TITLE = #{inteTitle,jdbcType=VARCHAR}
    , INTE_PERI = #{intePeri,jdbcType=VARCHAR}
    , INTE_STA_DATE = #{inteStaDate,jdbcType=DATE}
    , INTE_END_DATE = #{inteEndDate,jdbcType=DATE}
    , INTE_CONT_PERSON = #{inteContPerson,jdbcType=VARCHAR}
    , INTE_EMAIL = #{inteEmail,jdbcType=VARCHAR}
    , INTE_PH = #{intePh,jdbcType=VARCHAR}
    , INTE_COMP_NM = #{inteCompNm,jdbcType=VARCHAR}
    , INTE_COMP_ADDR = #{inteCompAddr,jdbcType=VARCHAR}
    , INTE_REGION = #{inteRegion,jdbcType=VARCHAR}
    , INTE_INTRD = #{inteIntrd,jdbcType=CLOB}
		WHERE INTE_CD = #{inteCd,jdbcType=VARCHAR}

UPDATE INTERNSHIP
SET INTE_VIEWS = INTE_VIEWS + 1
WHERE INTE_CD = #{inteCd,jdbcType=VARCHAR}

mapper.insert:
INSERT INTO INTERNSHIP (
    INTE_CD
    , ATCH_FILE_ID
    , INTE_TITLE
    , INTE_PERI
    , INTE_STA_DATE
    , INTE_END_DATE
    , INTE_CONT_PERSON
    , INTE_EMAIL
    , INTE_PH
    , INTE_COMP_NM
    , INTE_COMP_ADDR
    , INTE_REGION
    , INTE_INTRD
    , INTE_VIEWS
) VALUES (
INTERNSHIP_SEQ.NEXTVAL
, #{atchFileId,jdbcType=NUMERIC}
, #{inteTitle,jdbcType=VARCHAR}
, #{intePeri,jdbcType=VARCHAR}
, #{inteStaDate,jdbcType=DATE}
, #{inteEndDate,jdbcType=DATE}
, #{inteContPerson,jdbcType=VARCHAR}
, #{inteEmail,jdbcType=VARCHAR}
, #{intePh,jdbcType=VARCHAR}
, #{inteCompNm,jdbcType=VARCHAR}
, #{inteCompAddr,jdbcType=VARCHAR}
, #{inteRegion,jdbcType=VARCHAR}
, #{inteIntrd,jdbcType=CLOB}
, 0
)

mapper.select:
SELECT B.* FROM( SELECT ROWNUM RNUM, A.* FROM( SELECT INTE_CD , ATCH_FILE_ID , INTE_TITLE , INTE_PERI , INTE_STA_DATE , INTE_END_DATE , INTE_CONT_PERSON , INTE_EMAIL , INTE_PH , INTE_COMP_NM , INTE_COMP_ADDR , INTE_VIEWS , INTE_REGION , INTE_INTRD , INTE_DELETE FROM INTERNSHIP WHERE INTE_DELETE IS NULL
ORDER BY INTE_CD DESC ) A ) B
WHERE RNUM >= #{startRow} AND RNUM <=#{endRow}

WITH ATCH_FILE_VIEW AS( SELECT * FROM ATCH_FILE INNER JOIN ATCH_FILE_DETAIL USING(ATCH_FILE_ID) ) SELECT I.INTE_CD , I.ATCH_FILE_ID , I.INTE_TITLE , I.INTE_PERI , I.INTE_STA_DATE , I.INTE_END_DATE , I.INTE_CONT_PERSON , I.INTE_EMAIL , I.INTE_PH , I.INTE_COMP_NM , I.INTE_COMP_ADDR , I.INTE_VIEWS , I.INTE_REGION , I.INTE_INTRD , I.INTE_DELETE
, V.ATCH_FILE_ID, V.FILE_SN, V.FILE_STRE_COURS , V.STRE_FILE_NM, V.ORIGNL_FILE_NM, V.FILE_EXTSN , V.FILE_SIZE, V.FILE_MIME
, V.CREAT_DT , V.USE_AT FROM INTERNSHIP I LEFT OUTER JOIN ATCH_FILE_VIEW V ON I.ATCH_FILE_ID = V.ATCH_FILE_ID WHERE I.INTE_CD = #{inteCd}
